#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <unistd.h>
#include "http.h"

void helloHandler(HttpRequest* req, HttpResponse* resp) {
    // Obtener método HTTP
    printf("Método: %s\n", GetRequestMethod(req));
    
    // Obtener URL
    printf("URL: %s\n", GetRequestURL(req));
    
    // Obtener cuerpo (con longitud)
    //printf("Body (bytes): %s\n", GetRequestBody(req, &req->body_len));
    

    // Obtener cuerpo (con longitud)
    printf("Header (Authorization): %s\n", GetRequestHeader(req, "Authorization"));


    // Obtener dirección remota
    //printf("Dirección remota: %s\n", GetRequestRemoteAddr(req));
    
    // Obtener header específico
    printf("Content-Type: %s\n", GetRequestHeader(req, "Content-Type"));
    
    //SetResponseStatusCode(resp, 404);
    SetResponseContentType(resp, "image/png");
    SetResponseBinary(resp, "");
}


void test(HttpRequest* req, HttpResponse* resp) {
    SetResponseStatusCode(resp, 200);
    SetResponseContentType(resp, "text/html; charset=utf-8");  // Aquí el cambio importante
    SetResponseText(resp, "<!DOCTYPE html><html><head><meta charset=\"UTF-8\"></head><body>¡Hola Mundo!</body></html>");
}

int main() {
    // Registrar handler para la ruta raíz
    RegisterHandler("/", helloHandler);
    RegisterHandler("/test", test);
    
    // Iniciar servidor en el puerto 8123
    StartServerWithIPFilter("8123", 0);
    
    printf("Servidor iniciado en http://localhost:8123\n");
    printf("Presiona Ctrl+C para detenerlo...\n");
    
    // Mantener el programa en ejecución
    while(1) {
        sleep(1);
    }
    
    return 0;
}


